package week1.day2;

public class OddOrEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int y = 20;
			
		if (y%2 ==0)
		{
			System.out.println("The given number " +y+ " is even");
		}
		else {
			System.out.println("The given number " +y+ " is odd");
		}
	}

}
